						   	   genre[i].slice(index + 2);						}    				}    				genreCorrected += (genre[i] + " ");     			}     			genreCorrected = genreCorrected.substring(0, genreCorrected.length - 1);    			artistInfo[0] = new Array(name, id, genreCorrected, onTour);    		}    		checkInput(artistInfo, 1);        }    });}</script></head></html>